import { Component,OnInit } from '@angular/core';

@Component({
  selector: 'app-excluir',
  templateUrl: './excluir.component.html',
  styleUrls: ['./excluir.component.scss']
})
export class ExlcuirComponent implements OnInit {

  tarefas:any[] = []
  constructor(){}

  ngOnInit(): void {
    this.tarefas = [
      {id:0, nome: 'Lavar o carro', concluída:Boolean},
      {id:0, nome: 'Limpar a casa', concluída:Boolean},
      {id:0, nome: 'Limpar o PC', concluída:Boolean},
      {id:0, nome: 'Fazer compras', concluída:Boolean},
    ]
  }

  adicionar(nomeTarefa:string){
    if(nomeTarefa.trim().length ==0){
    return;
  }

  const tarefajaexiste = this.tarefas.find(item => item.nome.toLowerCase() == nomeTarefa.toLowerCase())

  if (!tarefajaexiste){
    this.tarefas.push({id: this.tarefas.length, nome:nomeTarefa, concluida:Boolean})
  }
}
}
