import { Component,OnInit } from '@angular/core';


@Component({
  selector: 'app-criar',
  templateUrl: './criar.component.html',
  styleUrls: ['./criar.component.scss']
})
export class CriarComponent implements OnInit {

  tarefas:any[] = []
  constructor(){}

  ngOnInit(): void {
    this.tarefas = [
      {id:0, nome: 'Lavar o carro', concluída:Boolean},
      {id:0, nome: 'Limpar a casa', concluída:Boolean},
      {id:0, nome: 'Limpar o PC', concluída:Boolean},
      {id:0, nome: 'Fazer compras', concluída:Boolean},
    ]
  }

  adicionar(nomeTarefa:string){
    this.tarefas.push({id: this.tarefas.length, nome:nomeTarefa, concluida:Boolean})
  }
}
