import { Component,OnInit } from '@angular/core';

@Component({
  selector: 'app-concluir',
  templateUrl: './concluir.component.html',
  styleUrls: ['./concluir.component.scss']
})
export class CriarComponent implements OnInit {

  tarefas:any[] = []
  constructor(){}

  ngOnInit(): void {
    this.tarefas = [
      {id:0, nome: 'Lavar o carro', concluída:Boolean},
      {id:0, nome: 'Limpar a casa', concluída:Boolean},
      {id:0, nome: 'Limpar o PC', concluída:Boolean},
      {id:0, nome: 'Fazer compras', concluída:Boolean},
    ]
  }

  concluir(id:number){

  const tarefaEcontrada = this.tarefas.find(item => item.id ==id)

  if (tarefaEcontrada){
    tarefaEcontrada.concluida = !tarefaEcontrada.concluida
  }
}
}
