import { Component,OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';


@Component({
  selector: 'app-editar',
  templateUrl: './editar.component.html',
  styleUrls: ['./editar.component.scss']
})
export class EditarComponent implements OnInit {

  TAREFA_KEY = 'tarefa_key'
  atividades : any[] = []
  constructor() { }

  ngOnInit(): void {
     const tarefas = localStorage.getItem(this.TAREFA_KEY)
    if (tarefas){
      this.atividades = JSON.parse(tarefas)
    }
  }

  editar(id: number, novoNome: string, concluida: boolean) {
    const tarefaEncontrada = this.atividades.find(item => item.id == id);
    if (tarefaEncontrada) {
      tarefaEncontrada.nome = novoNome;
      tarefaEncontrada.concluida = concluida;
      this.salvarLista();
    }
  }
  private salvarLista(){
    localStorage.setItem(this.TAREFA_KEY, JSON.stringify(this.atividades))
  }

}
