import { Component,OnInit } from '@angular/core';

@Component({
  selector: 'app-editar',
  templateUrl: './editar.component.html',
  styleUrls: ['./editar.component.scss']
})
export class EditarComponent implements OnInit {

  TAREFA_KEY = 'tarefa_key'
  atividades : any[] = []
  constructor() { }

  ngOnInit(): void {
     const tarefas = localStorage.getItem(this.TAREFA_KEY)
    if (tarefas){
      this.atividades = JSON.parse(tarefas)
    }
  }

  editar(nomeTarefa: string) {
    if (nomeTarefa.trim().length == 0){
      return;
    }
    const tarefaEncontradaIndex = this.atividades.findIndex(item => item.nome.toLowerCase() == nomeTarefa.toLowerCase())

    if (tarefaEncontradaIndex !== -1) {
      // Atualiza a tarefa existente
      this.atividades[tarefaEncontradaIndex].concluida = false; // Altere aqui a propriedade que deseja atualizar
    } else {
      // Adiciona uma nova tarefa
      this.atividades.push({id: this.atividades.length, nome: nomeTarefa, concluida: false})
    }
    this.salvarLista();
}


  concluir(id: number) {
    const tarefaEncontrada = this.atividades.find( item => item.id == id)

    if (tarefaEncontrada){
      tarefaEncontrada.concluida = !tarefaEncontrada.concluida
      this.salvarLista()
    }

  }

  private salvarLista(){
    localStorage.setItem(this.TAREFA_KEY, JSON.stringify(this.atividades))
  }

  editarTarefa(){

  }
}
