import { Component,OnInit } from '@angular/core';
import { ITarefaEnum } from './../../models/ITarefas';
import { BaseService } from 'src/app/servicos/base.service';


@Component({
  selector: 'app-criar',
  templateUrl: './criar.component.html',
  styleUrls: ['./criar.component.scss']
})
export class CriarComponent implements OnInit {

  tarefas!: ITarefaEnum[];
  novaTarefa: ITarefaEnum = { id: null, titulo: '', descricao: '', concluida: false };
  tarefaSelecionada!: ITarefaEnum;

  constructor(private BaseService: BaseService) { }

  ngOnInit() {
    this.tarefas = this.BaseService.tarefas;
  }

  criarTarefa() {
    this.novaTarefa.id = this.tarefas.length + 1;
    this.tarefas.push(this.novaTarefa);
    this.novaTarefa = { id: null, titulo: '', descricao: '', concluida: false };
  }
}
